sourcesContent:
import User from "../models/user"
import jwt from 'jsonwebtoken'


export const signUp = async (req, res) => {
    const {email, name, password} = req.body
    try {
        const exisUser = await User.findOne({email}).exec();
        if (exisUser) {
            return res.status(400).json({
                message:"User Ä‘Ã£ tá»“n táº¡i"
            })
        }
        const user = await User({email, password, name}).save()
        res.json({
            user:{
                _id:user._id,
                email: user.email,
                username: user.name
            }
    })
    } catch (error) {
        res.status(400).json({message:"Lá»—i rá»“i"})
    }
}

export const signIn = async (req, res) => {
    const {email, password} = req.body
    try {
        const user = await User.findOne({email}).exec();
        if (!user) {
            return res.status(400).json({
                message:"User khÃ´ng tá»“n táº¡i"
            })
        }
        
        if (!user.authenticate(password)) {
            return res.status(400).json({
                message:"Máº­t kháº©u khÃ´ng Ä‘Ãºng"
            })
        }
        const token = jwt.sign({_id: user._id}, "123456", {expiresIn: '1h'})
        res.json({
            token,
            user:{
                _id:user._id,
                email:user.email,
                name: user.name
            }
        })
    } catch (error) {
        res.status(400).json({
            message: "ÄÄƒng nháº­p tháº¥t báº¡i"
        })
    }
}

export const userById = async (req, res, next, id) => {
    try {
        const userById = await User.findById(id).exec();
        if (!userById) {
            res.status(400).json({
                message: "KhÃ´ng tÃ¬m tháº¥y user"
            })
        }

        req.profile = userById;
        req.profile.password = undefined;
        next();
    } catch (error) {
        console.log(error);
    }
}

