import User from "../models/user"
import jwt from 'jsonwebtoken'


export const signUp = async (req, res) => {
    const { email, name, password } = req.body
    try {
        const exisUser = await User.findOne({ email }).exec();
        if (exisUser) {
            return res.status(400).json({
                message: "User Ä‘Ã£ tá»“n táº¡i"
            })
        }
        const user = await User({ email, password, name }).save()
        res.json({
            user: {
                _id: user._id,
                email: user.email,
                username: user.name
            }
        })
    } catch (error) {
        res.status(400).json({ message: "Lá»—i rá»“i" })
    }
}

export const signIn = async (req, res) => {
    const { email, password } = req.body
    try {
        const user = await User.findOne({ email }).exec();
        if (!user) {
            console.log("Lá»—i: User khÃ´ng tá»“n táº¡i");
            return res.status(400).json({ message: "User khÃ´ng tá»“n táº¡i" });

        }
        if (!user.authenticate(password)) {
            return res.status(400).json({
                message: "Máº­t kháº©u khÃ´ng Ä‘Ãºng"
            })
        }
        const token = jwt.sign({ _id: user._id }, "123456", { expiresIn: '1h' })
        res.json({
            token,
            user: user
        })
    } catch (error) {
        res.status(400).json({
            message: "ÄÄƒng nháº­p tháº¥t báº¡i"
        })
    }
}

export const userById = async (req, res, next, id) => {
    try {
        const userById = await User.findById(id).exec();
        if (!userById) {
            res.status(400).json({
                message: "KhÃ´ng tÃ¬m tháº¥y user"
            })
        }

        req.profile = userById;
        req.profile.password = undefined;
        next();
    } catch (error) {
        console.log(error);
    }
}

export const getListUser = async (req, res) => {
    try {
        const users = await User.find();
        res.status(200).json(users);
    } catch (error) {
        res.status(500).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
    }
}

export const updateUser = async (req, res) => {
    try {
        const user = await User.findOneAndUpdate({ _id: req.params.id }, req.body, { new: true });

        // Táº¡o token má»›i giá»‘ng nhÆ° khi Ä‘Äƒng nháº­p
        const token = jwt.sign({ _id: user._id }, "123456", { expiresIn: '1h' });

        // Tráº£ vá» dá»¯ liá»‡u theo Ä‘á»‹nh dáº¡ng auth: {token, user: {}}
        res.json({
            token,
            user: user
        });
    } catch (error) {
        res.status(400).json({ message: "KhÃ´ng thá»ƒ sá»­a" });
    }
}

export const changePassword = async (req, res) => {
    try {
        const { oldPassword, password, _id } = req.body
        console.log(oldPassword, password, req.body);

        const user = await User.findOne({ _id: _id }).exec();
        console.log(user);

        if (!user.authenticate(oldPassword)) {
            return res.status(400).json({
                message: "Máº­t kháº©u khÃ´ng Ä‘Ãºng"
            })
        }
        // MÃ£ hÃ³a máº­t kháº©u má»›i
        const encryptedPassword = user.encryPassword(password);

        // Cáº­p nháº­t user vá»›i máº­t kháº©u Ä‘Ã£ mÃ£ hÃ³a
        const newUser = await User.findOneAndUpdate(
            { _id: _id },
