sourcesContent:
import express from "express";
import Field from "../models/field";
import mongoose from "mongoose";

const fieldRoutes = express.Router();

// 1ï¸ Láº¥y danh sÃ¡ch táº¥t cáº£ sÃ¢n bÃ³ng
fieldRoutes.get("/", async (req, res) => {
  try {
    const fields = await Field.find();
    res.status(200).json(fields);
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
});

fieldRoutes.get("/:id", async (req, res) => {
  try {
    const fields = await Field.findOne({ _id: req.params.id }).populate("foolballFieldId");
    res.status(200).json(fields);
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
});


// fieldRoutes.get("/footballId/:id", async (req, res) => {
//   try {

//     const footballFieldId = new mongoose.Types.ObjectId(req.params.id);  // Láº¥y ID tá»« request

//     // Sá»­ dá»¥ng aggregation Ä‘á»ƒ káº¿t há»£p cáº£ Field vÃ  TimeSlot trong má»™t truy váº¥n
//     const populatedFields = await Field.aggregate([
//       {
//         $match: { foolballFieldId: footballFieldId }  // Lá»c cÃ¡c Field cÃ³ footballFieldId trÃ¹ng vá»›i ID
//       },
//       {
//         $lookup: {
//           from: "timeslots",  // Thá»±c hiá»‡n join vá»›i collection TimeSlot
//           localField: "_id",   // DÃ¹ng _id cá»§a Field
//           foreignField: "fieldId",  // DÃ¹ng fieldId trong TimeSlot Ä‘á»ƒ join
//           as: "timeSlots"   // GÃ¡n káº¿t quáº£ vÃ o trÆ°á»ng "timeSlots"
//         }
//       }
//     ]);
//     res.status(200).json(populatedFields);
//   } catch (error) {
//     res.status(500).json({ message: error.message });
//   }
// });

fieldRoutes.get("/footballId/:id", async (req, res) => {
  try {
    const fields = await Field.find({ foolballFieldId: req.params.id }).populate("foolballFieldId");
    res.status(200).json(fields);
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
});


// 2ï¸ Táº¡o má»›i má»™t sÃ¢n bÃ³ng
fieldRoutes.post("/", async (req, res) => {
  try {
    const newField = new Field(req.body);
    const savedField = await newField.save();
    res.status(201).json(savedField);
  } catch (error) {
    res.status(400).json({ message: error.message });
  }
});

// 3ï¸ Cáº­p nháº­t lá»‹ch Ä‘áº·t sÃ¢n
fieldRoutes.patch("/:id", async (req, res) => {
  try {
    const updatedField = await Field.findByIdAndUpdate(req.params.id, req.body, { new: true });
    res.status(200).json(updatedField);
  } catch (error) {
    res.status(400).json({ message: error.message });
  }
});

// 4ï¸ XÃ³a má»™t sÃ¢n bÃ³ng
fieldRoutes.delete("/:id", async (req, res) => {
  try {
    const slots = await Field.findOneAndDelete({ _id: req.params.id });
    res.status(200).json(slots);
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
});

export default fieldRoutes;

