import mongoose from "mongoose";
import { ObjectId } from "mongodb";

const sanBongSchema = new mongoose.Schema(
  {
    name: { type: String, required: true },
    people: { type: Number, required: true }, // Sá»‘ ngÆ°á»i tá»‘i Ä‘a
