sourcesContent:
import mongoose from "mongoose";
import { ObjectId } from "mongodb";


const footballFieldSchema = new mongoose.Schema(
    {
        name: { type: String, required: true },
        image: { type: String, required: true },
        address: {
            province: { type: String, required: true },
            district: { type: String, required: true },
            ward: { type: String, required: true },
            detail: { type: String }
        },
        phone: { type: String, required: true },
        desc: { type: String },
        userId: {
            type: ObjectId,
            ref: "User"
        },
        status: { type: String, enum: ["Hoáº¡t Ä‘á»™ng", "Báº£o trÃ¬", "VÃ´ hiá»‡u hÃ³a", "Chá» xÃ¡c nháº­n"], default: "Chá» xÃ¡c nháº­n" },
        // timeSlot: {
        //     type: ObjectId,
        //     ref: "TimeSlot"
        // }
    },
    { timestamps: true }
);

export default mongoose.model("FootballField", footballFieldSchema);

