sourcesContent:
const express = require("express");
import Booking from "../models/booking"; // Import mÃ´ hÃ¬nh Booking

const bookingRouter = express.Router();

// Táº¡o má»™t booking má»›i
bookingRouter.post("/", async (req, res) => {
  try {
    const newBooking = await Booking.create(req.body); // Táº¡o booking má»›i tá»« dá»¯ liá»‡u trong body cá»§a request
    res.status(200).json(newBooking); // Tráº£ vá» booking má»›i táº¡o
  } catch (error) {
    res.status(500).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
  }
});

// Cáº­p nháº­t thÃ´ng tin má»™t booking (sá»­ dá»¥ng ID cá»§a booking)
bookingRouter.patch("/:id", async (req, res) => {
  try {
    const updatedBooking = await Booking.findByIdAndUpdate(req.params.id, req.body, { new: true });
    res.status(200).json(updatedBooking); // Tráº£ vá» booking Ä‘Ã£ Ä‘Æ°á»£c cáº­p nháº­t
  } catch (error) {
    res.status(400).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
  }
});

// Liá»‡t kÃª táº¥t cáº£ cÃ¡c booking
bookingRouter.get("/", async (req, res) => {
  try {
    const bookings = await Booking.find().sort({ createdAt: -1 });
    res.status(200).json(bookings); // Tráº£ vá» danh sÃ¡ch táº¥t cáº£ cÃ¡c booking
  } catch (error) {
    res.status(500).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
  }
});

bookingRouter.get("/:id", async (req, res) => {
  try {
    const bookings = await Booking.findOne({ _id: req.params.id }); // Láº¥y táº¥t cáº£ cÃ¡c booking
    res.status(200).json(bookings); // Tráº£ vá» danh sÃ¡ch táº¥t cáº£ cÃ¡c booking
  } catch (error) {
    res.status(500).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
  }
});

bookingRouter.get("/userId/:id", async (req, res) => {
  try {
    const bookings = await Booking.find({ user: req.params.id }); // Láº¥y táº¥t cáº£ cÃ¡c booking
    res.status(200).json(bookings); // Tráº£ vá» danh sÃ¡ch táº¥t cáº£ cÃ¡c booking
  } catch (error) {
    res.status(500).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
  }
});


bookingRouter.get("/footballField/:id", async (req, res) => {
  try {
    const bookings = await Booking.find({ footballField: req.params.id }); // Láº¥y táº¥t cáº£ cÃ¡c booking
    res.status(200).json(bookings); // Tráº£ vá» danh sÃ¡ch táº¥t cáº£ cÃ¡c booking
  } catch (error) {
    res.status(500).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
  }
});

bookingRouter.get("/:footballField/:date", async (req, res) => {
  try {
    const bookings = await Booking.find({ footballField: req.params.footballField, date:req.params.date }); // Láº¥y táº¥t cáº£ cÃ¡c booking
    res.status(200).json(bookings); // Tráº£ vá» danh sÃ¡ch táº¥t cáº£ cÃ¡c booking
  } catch (error) {
    res.status(500).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
  }
});

// XoÃ¡ má»™t booking theo ID
bookingRouter.delete("/:id", async (req, res) => {
  try {
    const deletedBooking = await Booking.findByIdAndDelete(req.params.id); // TÃ¬m vÃ  xÃ³a booking theo ID
    res.status(200).json(deletedBooking); // Tráº£ vá» booking Ä‘Ã£ xÃ³a
  } catch (error) {
    res.status(400).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
  }
});

export default bookingRouter;

