sourcesContent:
import mongoose from 'mongoose';
const { Schema } = mongoose;
import { ObjectId } from "mongodb";

// Enum cho Actor vÃ  NotificationType (Ä‘Ã£ giáº£i thÃ­ch á»Ÿ trÃªn)
const ActorType = {
    USER: 'user',
    MANAGER: 'manager',
    ADMIN: 'admin',
};

const NotificationType = {
    FIELD_BOOKED: 'field_booked',           // Äáº·t sÃ¢n thÃ nh cÃ´ng
    FIELD_BOOKING_FAILED: 'field_booking_failed',  // Äáº·t sÃ¢n tháº¥t báº¡i
    FIELD_CREATED: 'field_created',         // Táº¡o sÃ¢n bÃ³ng thÃ nh cÃ´ng
    OPPONENT_FOUND: 'opponent_found',       // TÃ¬m Ä‘á»‘i thá»§ thÃ nh cÃ´ng
    POSTED_OPPONENT: 'posted_opponent',     // ÄÄƒng bÃ i tÃ¬m Ä‘á»‘i thÃ nh cÃ´ng
    NEW_ORDER: 'new_order',                 // ÄÆ¡n hÃ ng má»›i
    FIELD_REGISTRATION_REQUEST: 'field_registration_request', // YÃªu cáº§u Ä‘Äƒng kÃ½ sÃ¢n bÃ³ng
    USER_FEEDBACK: 'user_feedback',         // Pháº£n há»“i tá»« ngÆ°á»i dÃ¹ng
};

// Schema thÃ´ng bÃ¡o
const notificationSchema = new Schema({
    actor: {
        type: String,
        enum: Object.values(ActorType),
        required: true,
    },
    notificationType: {
        type: String,
        enum: Object.values(NotificationType),
        required: true,
    },
    title: {
        type: String,
        required: true,
    },
    content: {
        type: String,
    },
    bookingId: {
        type: ObjectId,
        ref: 'Booking',
    },
    targetUser: {
        type: ObjectId,
        ref: 'User', // Náº¿u cáº§n, báº¡n cÃ³ thá»ƒ liÃªn káº¿t vá»›i model User
        required: false,
    },
    footballfield: {
        type: ObjectId,
        ref: 'FootballField',
        required: false,
    },
    club_A: {
        type: ObjectId,
        ref: 'Team',
    },
    club_B: {
        type: ObjectId,
        ref: 'Team',
    },
    read: {
        type: Boolean,
        default: false,
    },
}, { timestamps: true });

