import express from 'express';
import { Notification } from '../models/notification'; // Import Notification model
import footballField from '../models/footballField';
// import { FootballField } from '../models/footballField'; // Import Notification model

const notificationRouter = express.Router();

// Route thÃªm má»›i thÃ´ng bÃ¡o
notificationRouter.post("/", async (req, res) => {
    try {
        // Táº¡o má»›i thÃ´ng bÃ¡o tá»« body request
        const newNotification = new Notification(req.body);

        // LÆ°u thÃ´ng bÃ¡o vÃ o cÆ¡ sá»Ÿ dá»¯ liá»‡u
        const savedNotification = await newNotification.save();

        // Tráº£ vá» thÃ´ng bÃ¡o Ä‘Ã£ Ä‘Æ°á»£c lÆ°u
        res.status(201).json(savedNotification);
    } catch (error) {
        console.error(error);
        res.status(400).json({ message: error.message });
    }
});

notificationRouter.get("/", async (req, res) => {
    try {
        const notification = await Notification.find().populate("bookingId footballfield"); // Láº¥y táº¥t cáº£ cÃ¡c booking
        res.status(200).json(notification); // Tráº£ vá» danh sÃ¡ch táº¥t cáº£ cÃ¡c booking
    } catch (error) {
        res.status(500).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
    }
});

notificationRouter.get("/detail/:id", async (req, res) => {
    try {
        const notification = await Notification.findOne({ _id: req.params.id }).populate("bookingId footballfield"); // Láº¥y táº¥t cáº£ cÃ¡c booking
        res.status(200).json(notification); // Tráº£ vá» danh sÃ¡ch táº¥t cáº£ cÃ¡c booking
    } catch (error) {
        res.status(500).json({ message: error.message }); // Náº¿u cÃ³ lá»—i, tráº£ vá» thÃ´ng bÃ¡o lá»—i
    }
});

notificationRouter.get("/:userId/:role", async (req, res) => {
    try {
        const { userId, role } = req.params;
        if (!userId || !role) {
            return res.status(400).json({ error: "Missing userId or role" });
        }

        const data = await Notification.find({ targetUser: userId, actor: role }).populate("bookingId footballfield club_A club_B");
        // const football = await footballField.findOne({ userId: userId });
        
        // if (!football || !data) {
        //     return res.status(404).json({ error: "Data not found" });
        // }
        // let filteredData = [];
        // if (role === "manager") {
        //     const newDataForUser = data.filter(
        //         (item) => item.actor === "user" && String(item.targetUser) === userId
        //     );
        //     const newDataForManager = data.filter(
        //         (item) => item.actor === "manager" && String(item.footballfield?._id) === String(football._id)
        //     );

        //     filteredData = [...newDataForUser, ...newDataForManager];
        //    } else {
        //     filteredData = data.filter(
        //         (item) => item.actor === "user" && String(item.targetUser) === userId
        //     );
        // }

        res.status(200).json(data);
    } catch (error) {
        console.error("Error fetching notifications:", error);
        res.status(500).json({ error: "Internal Server Error" });
    }
});


notificationRouter.patch("/:id", async (req, res) => {
    try {
        const notification = await Notification.findByIdAndUpdate(req.params.id, req.body, { new: true });
        res.status(200).json(notification);
    } catch (error) {
        res.status(400).json({ message: error.message });
    }
});

export default notificationRouter;

