import express from "express";
import Team from "../models/team"; // Äáº£m báº£o ráº±ng báº¡n Ä‘Ã£ import Ä‘Ãºng model

const teamRouter = express.Router();

// 1. Láº¥y táº¥t cáº£ cÃ¡c Ä‘á»™i bÃ³ng
teamRouter.get("/", async (req, res) => {
    try {
        const teams = await Team.find();
        res.status(200).json(teams);
    } catch (error) {
        res.status(500).json({ message: error.message });
    }
});

// 2. Láº¥y Ä‘á»™i bÃ³ng theo id
teamRouter.get("/:id", async (req, res) => {
    try {
        const team = await Team.findById(req.params.id).populate("user members.user");
        res.status(200).json(team);
    } catch (error) {
        res.status(500).json({ message: error.message });
    }
});

teamRouter.get("/byUser/:userId", async (req, res) => {
    try {
        // TÃ¬m cÃ¡c team mÃ  user lÃ  thÃ nh viÃªn (cÃ³ trong members.user)
        const teams = await Team.find({ 
            "members.user": req.params.userId 
        }).populate('user').populate('members.user');
        
        res.status(200).json(teams);
    } catch (error) {
        res.status(500).json({ message: error.message });
    }
});

// 3. Táº¡o má»™t Ä‘á»™i bÃ³ng má»›i
teamRouter.post("/", async (req, res) => {
    try {
        const newTeam = new Team(req.body); // Táº¡o má»›i Ä‘á»™i bÃ³ng tá»« body request
        const savedTeam = await newTeam.save(); // LÆ°u Ä‘á»™i bÃ³ng vÃ o cÆ¡ sá»Ÿ dá»¯ liá»‡u
        res.status(201).json(savedTeam); // Tráº£ vá» Ä‘á»™i bÃ³ng Ä‘Ã£ lÆ°u vá»›i mÃ£ tráº¡ng thÃ¡i 201
    } catch (error) {
        res.status(400).json({ message: error.message }); // Tráº£ vá» lá»—i náº¿u cÃ³ váº¥n Ä‘á»
    }
});

// 4. Cáº­p nháº­t thÃ´ng tin Ä‘á»™i bÃ³ng theo id
teamRouter.patch("/:id", async (req, res) => {
    try {
        const updatedTeam = await Team.findByIdAndUpdate(
            req.params.id,
            req.body,
            { new: true }
        );
        res.status(200).json(updatedTeam);
    } catch (error) {
        res.status(400).json({ message: error.message }); // Tráº£ vá» lá»—i náº¿u cÃ³ váº¥n Ä‘á»
    }
});
// 5. XÃ³a Ä‘á»™i bÃ³ng theo id
teamRouter.delete("/:id", async (req, res) => {
    try {
        const deletedTeam = await Team.findByIdAndDelete(req.params.id);
        if (!deletedTeam) {
